sql_count($result); $i++)\n{\n    $row = sql_fetch($result);\n    ?&gt;\n    &lt;div&gt;\n    	&lt;span&gt;\n    		&lt;?php echo $row[\'productNm\'];?&gt;\n    	&lt;/span&gt;\n    &lt;/div&gt;\n    &lt;?php \n}\n?&gt;\n&lt;div class=&quot;page_num&quot;&gt;\n\n&lt;?php\nif(5 &lt; $page)\n{\n    ?&gt;\n    &lt;a href=&quot;javascript:pageMove(1);&quot;&gt; &lt;&lt; &lt;/a&gt;\n    &lt;a href=&quot;javascript:pageMove(&lt;?=$s_page-1?&gt;);&quot;&gt; &lt; &lt;/a&gt;\n    &lt;?php \n}\n$pagingCnt = 0;\nif($e_page != 0)\n{\n    for ($p=$s_page; $p&lt;=$e_page; $p++)\n    {\n        $pagingCnt ++;\n        ?&gt;\n        &lt;a href=&quot;javascript:pageMove(&lt;?=$p?&gt;);&quot; id=&quot;p_&lt;?=$p?&gt;&quot; class=&quot;p_btn&quot;&gt;&lt;?=$p?&gt;&lt;/a&gt;\n 	    &lt;?php\n    }\n}\nelse\n{\n    ?&gt;\n    &lt;a href=&quot;javascript:pageMove(1);&quot;&gt;1&lt;/a&gt;\n    &lt;?php\n}\nif($pageNum != $page &amp;&amp; $pageNum &gt; 5)\n{\n    if($pagingCnt &gt; 4)\n    {\n        if($e_page+1 &gt; $pageNum)\n        {\n            ?&gt;\n             &lt;a href=&quot;javascript:pageMove(&lt;?=$pageNum?&gt;);&quot;&gt; &gt; &lt;/a&gt;\n            &lt;a href=&quot;javascript:pageMove(&lt;?=$pageNum?&gt;);&quot;&gt; &gt;&gt; &lt;/a&gt; \n            &lt;?php\n        }\n        else\n        {\n            ?&gt;\n            &lt;a href=&quot;javascript:pageMove(&lt;?=$e_page+1?&gt;);&quot;&gt; &gt; &lt;/a&gt;\n            &lt;a href=&quot;javascript:pageMove(&lt;?=$pageNum?&gt;);&quot;&gt; &gt;&gt; &lt;/a&gt;\n            &lt;?php\n        }\n    }\n}\n?&gt;\n&lt;/div&gt;\n','20200302175056'),(5,'Ajax 페이징','','','function toAjax()\n{\n	var queryString = $(&quot;form[name=shopForm]&quot;).serialize();\n    $.ajax({\n        type : \'get\',\n        url : \'/delivery-service/?\'+queryString,\n        error: function(xhr, status, error){\n            alert(error);\n        },\n        success : function(data){\n            var result = $(&quot;#store&quot;).html(data).find(\