ile&quot;;\nvar coupang_ins_ios = &quot;https://itunes.apple.com/app/id454434967&quot;;\n\n\nvar fresh_play = &quot;gssmapp://default?movemenu=1&amp;moveurl=http://m.gsfresh.com/&quot;;\nvar fresh_ins_android = &quot;market://details?id=com.gsretail.android.smapp&quot;;\nvar fresh_ins_ios = &quot;https://itunes.apple.com/app/id813494275&quot;;\n\n\n\n\n//어플 실행\nfunction appStart(appName){\n \n	if(&quot;android&quot; == OSName || &quot;ios&quot; == OSName)\n	{\n		if(appName == &quot;eleven&quot;)\n		{\n    		location.href = eleven_play;\n		}\n		else if(appName == &quot;coupang&quot;)\n		{\n    		location.href = coupang_play;\n		}\n		else if(appName == &quot;fresh&quot;)\n		{\n    		location.href = fresh_play;\n		} \n		setTimeout(&quot;checkApplicationInstall_callback(\'&quot;+appName+&quot;\')&quot;, 1500);\n	}\n	else\n	{\n		return;\n	}\n}\n\n//Android의경우 어플실행 확인 및 설치페이지 이동\n\n//IOS의 경우 어플실행명령 후 1.5초 후에 설치페이지 이동에 대한 확인창 발생\n\nfunction checkApplicationInstall_callback(appName) \n{\n	if(&quot;android&quot;==OSName)\n	{\n		if(appName == &quot;eleven&quot;)\n		{\n    		location.href = eleven_ins_android;\n		}\n		else if(appName == &quot;coupang&quot;)\n		{\n    		location.href = coupang_ins_android;\n		}\n		else if(appName == &quot;fresh&quot;)\n		{\n    		location.href = fresh_ins_android;\n		} \n	}\n	if(&quot;ios&quot; == OSName)\n	{\n		if(appName == &quot;eleven&quot;)\n		{\n    		location.href = eleven_ins_ios;\n		}\n		else if(appName == &quot;coupang&quot;)\n		{\n    		location.href = coupang_ins_ios;\n		}\n		else if(appName == &quot;fresh&quot;)\n		{\n    		location.href = fresh_ins_ios;\n		} \n	}\n}\n','','20200714163501'),(33,'JSP Oracle 연동','','','','&lt;%@ page import=&quot;java.sql.*&quot;%&gt;\n&lt;%@ page import=&quot;java.util.*&quot;%&gt;\n&lt;%@page import=&quot;java.io.File&quot;%&gt;\n&lt;%@ page language=&quot;java&quot; contentType=&quot;text/html; charset=UTF-8&quot; pageEncoding=&quot;UTF-8&quot;%&gt;\n\n&lt;%\nConnection conn = null;\nPreparedState